import{_ as a,o as r,c as s,k as e,a as t}from"./chunks/framework.7f03344f.js";const y=JSON.parse('{"title":"补订《插件开发日记》","description":"","frontmatter":{},"headers":[],"relativePath":"README.md","filePath":"README.md","lastUpdated":1698403164000}'),n={name:"README.md"},i=e("h1",{id:"补订《插件开发日记》",tabindex:"-1"},[t("补订《插件开发日记》 "),e("a",{class:"header-anchor",href:"#补订《插件开发日记》","aria-label":'Permalink to "补订《插件开发日记》"'},"​")],-1),o=e("p",null,"《PluginDiary 插件开发教程》，又名《插件开发日记》，乃是 ThatRarityEG 道席所著。该教程将 Bukkit 插件开发的流程向新手娓娓道来，深入浅出，历来为人称颂。可惜作者不满于网易代理中国区《我的世界》后的所作所为，于二〇二三年初愤而离去，永久注销了自己在各个平台的社交媒体账号，彻底退出了 Minecraft 编程开发社区。",-1),l=e("p",null,"自从 ThatRarityEG 小马道席退出 Minecraft 编程开发社区以后，其著作《PluginDiary 插件开发教程》失修。图床蓦然停运，致使原图全部丢失。服务端不断更新，于是部分代码在高版本无法运作。如此种种，并不是因为没有人愿意帮她修订、维护教程，而是因为无人有权修订。教程网站托管在 Cloudflare Pages 服务上，读取小马的个人仓库生成网页。该仓库没有 Outside Collaborator，因此想要修改教程内容的开发者不能直接将 Commit 推送至仓库，只能发送 Pull Request 以俟小马审阅。小马离尘以后，不再使用原有的 GitHub 账号，无人合并 Pull Request，于是仓库内容就一成不变，网站亦然。",-1),d=e("p",null,"为了承接小马之坠绪，让她的学说不至失传，我们数位有意愿维护该教程者讨论后，决定设立 InsidePlugins 小组。意在依据 CC BY 3.0 协议，将教程的仓库公开化，以便协同维护。图片丢失，我们就补图。代码缺损，我们就增续。希望能让小马的照临洒遍瀛寰，辉曜万年！",-1),p=e("p",null,[t("教程原地址："),e("a",{href:"https://plugin-diary.pages.dev",target:"_blank",rel:"noreferrer"},"https://plugin-diary.pages.dev")],-1),c=e("p",null,[t("教程新地址："),e("a",{href:"https://plugindiary.pvpin.org/",target:"_blank",rel:"noreferrer"},"https://plugindiary.pvpin.org/")],-1),_=[i,o,l,d,p,c];function u(h,f,g,m,E,P){return r(),s("div",null,_)}const R=a(n,[["render",u]]);export{y as __pageData,R as default};
